import React from 'react'
import Dice from './Dice'
import club from '../assets/club.png'
import king from '../assets/king.png'
import queen from '../assets/queen.png'
import heart from '../assets/heart.png'
import diamond from '../assets/diamond.png'
import spade from '../assets/spade.png'

import Card from 'react-bootstrap/Card';

function DiceList(){
  const [diceData, setDiceData] = useState();

  const baseURL = 'http://localhost:8080/roll-dice';

  useEffect(() => {
    axios.get(baseURL).then((response) => {
      setDiceData(response.data);
    });
  }, []);

  //const {KING, QUEEN, CLUB, HEART, DIAMOND, SPADE} = diceData
  return (
    <div className='container'>
         <div className='row'> 
          <Dice name='Club' image={club}  />  

          <Dice  name='King' image={king}  />  

          <Dice  name='Queen' image={queen}  />  

          <Dice  name='Spade' image={spade}  />  

          <Dice name='Diamond' image={diamond}  />  

          <Dice  name='Heart' image={heart}  /> 

          </div>


        </div>
  )
}

export default DiceList
